import React, {useState} from 'react';
import './App.css';
import CalculatorService from './calculator.service'

function App() {

  const [calculate, concatenateNumber, Sum, Sub, Multiply, Divide] = CalculatorService();

  const [txtNumber, setTxtNumber] = useState('0');

  const [number1, setNumber1] = useState(0);

  function addNumber(number){
    setTxtNumber(txtNumber + number);
  }

  function defineOperation(op) {
    setTxtNumber(op);
  }

  return (
        <div className="calculator">
          <div className="result">
            <div className="result-number" >
              {txtNumber}
            </div>
          </div>
          <div className="buttons">

            <div className="row1">
              <button onClick={() => addNumber('C') }>C</button>
              <button onClick={() => defineOperation('/') }>/</button>
            </div>

            <div className="row2">
              <button onClick={() => addNumber('7') }>7</button>
              <button onClick={() => addNumber('8') }>8</button>
              <button onClick={() => addNumber('9') }>9</button>
              <button onClick={() => defineOperation('*') }>*</button>
              <button onClick={() => addNumber('4') }>4</button>
              <button onClick={() => addNumber('5') }>5</button>
              <button onClick={() => addNumber('6') }>6</button>
              <button onClick={() => defineOperation('-') }>-</button>
              <button onClick={() => addNumber('1') }>1</button>
              <button onClick={() => addNumber('2') }>2</button>
              <button onClick={() => addNumber('3') }>3</button>
              <button onClick={() => defineOperation('+') }>+</button>
            </div>

            <div className="row3">
            <button onClick={() => addNumber('0') }>0</button>
            <button onClick={() => addNumber('.') }>.</button>
            <button onClick={() => defineOperation('=') }>=</button>
            </div>

          </div>
        </div>
  );
}

export default App;
